%% Load and analyse the means and vars for the circular data% toolboxaddpath("./circ_toolbox");% load in filesf_act = fopen("./sing_beta_2/runA/changes_for_batch_a_log.bin");f_ring = fopen("./sing_beta_2/runA/ring_direction_av_log.bin");% get datad_act = fread(f_act,"double");d_ring = fread(f_ring,"double");vars = [];means = [];for offset = 0:10:600  diff = d_act(100000:590000-offset)-d_ring(100000+offset:590000);  vars = [vars; circ_std(diff.*pi./180)];  means = [means; circ_mean(diff.*pi./180)];endvars = vars./pi.*180;means = means./pi.*180;[min_var, idx] = min(vars);min_var means(idx)