%% Load and analyse the non-parametric tests for the circular data% circular statistics toolbox required - place in ./circ_toolbox% Download here: https://uk.mathworks.com/matlabcentral/fileexchange/10676-circular-statistics-toolbox--directional-statistics-addpath("./circ_toolbox");% read data insing1 = csvread("./sing_beta_p5/mean_and_std.csv");pano1 = csvread("./pano_beta_p5/mean_and_std.csv");twobar1 = csvread("./2bar_beta_p5/mean_and_std.csv");sing2 = csvread("./sing_beta_2/mean_and_std.csv");pano2 = csvread("./pano_beta_2/mean_and_std.csv");twobar2 = csvread("./2bar_beta_2/mean_and_std.csv");disp("Pano : Single")circ_cmtest(sing1(:,2).*pi./180,pano1(:,2).*pi./180)disp("Pano : 2bar")circ_cmtest(twobar1(:,2).*pi./180,pano1(:,2).*pi./180)disp("Pano : Pano2")circ_cmtest(pano2(:,2).*pi./180,pano1(:,2).*pi./180)disp("Single : 2bar")circ_cmtest(sing1(:,2).*pi./180,twobar1(:,2).*pi./180)disp("Single : Single2")circ_cmtest(sing1(:,2).*pi./180,sing2(:,2).*pi./180)disp("2bar : 2bar2")circ_cmtest(twobar1(:,2).*pi./180,twobar2(:,2).*pi./180)disp("Pano2 : Single2")circ_cmtest(sing2(:,2).*pi./180,pano2(:,2).*pi./180)disp("Pano2 : 2bar2")circ_cmtest(pano2(:,2).*pi./180,twobar2(:,2).*pi./180)disp("Single2 : 2bar2")circ_cmtest(sing2(:,2).*pi./180,twobar2(:,2).*pi./180)