#PARNAME=dir #LOC=1,1

def connectionFunc( srclocs, dstlocs, dir ):

	import math

	i = 0
	out = []
	for srcloc in srclocs:
		if dir == 0:
			dst = i + 1
		if dir == 1:
			dst = i - 1
		if dst >= len(srclocs):
				dst = dst - len(srclocs)
		if dst < 0:
			dst = len(srclocs) + dst
		conn = (i,dst,0,1.0)
		out.append(conn)
		i = i + 1
	return out

