#PARNAME=size #LOC=1,1 #PARNAME=scale #LOC=2,1 #PARNAME=inhib #LOC=3,1 #HASWEIGHT
def connectionFunc( srclocs, dstlocs, size, scale, inhib ):
import math
i = 0 out = [] for srcloc in srclocs: j = 0 # inhibit to all for dstloc in dstlocs: conn = (i,j,0,-inhib) out.append(conn) j = j + 1 # link to nearest neighbours excite loc = i if loc < 0: loc = size + loc if loc >= size: loc = loc - size conn = (i,loc,0,1.0*scale+inhib) out.append(conn) loc = i-1 if loc < 0: loc = size + loc if loc >= size: loc = loc - size conn = (i,loc,0,0.5*scale+inhib) out.append(conn) loc = i+1 if loc < 0: loc = size + loc if loc >= size: loc = loc - size conn = (i,loc,0,0.5*scale+inhib) out.append(conn) loc = i+2 if loc < 0: loc = size + loc if loc >= size: loc = loc - size conn = (i,loc,0,0.25*scale+inhib) out.append(conn) loc = i-2 if loc < 0: loc = size + loc if loc >= size: loc = loc - size conn = (i,loc,0,0.25*scale+inhib) out.append(conn) i = i + 1 return out

