#PARNAME=rowlen #LOC=1,1 #PARNAME=number_outputs #LOC=2,1
def connectionFunc( srclocs, dstlocs, rowlen, num_output ):
import math
i = 0 out = [] inds_per_out = rowlen/num_output for srcloc in srclocs: out_ind = math.floor((i%rowlen)/inds_per_out) conn = (i,out_ind,0) out.append(conn) i = i + 1 return out

